// src/components/Room.js
import { Chessboard } from "react-chessboard";
import React, { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import Chat from "./Chat";
import { Chess } from "chess.js";

const SOCKET_SERVER_URL = "http://localhost:4000";

const Room = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const username = localStorage.getItem("username") || "";
  const [role, setRole] = useState(null);
  const [players, setPlayers] = useState({ white: null, black: null });
  const [turn, setTurn] = useState("white");
  const [fen, setFen] = useState("start");
  const [highlightSquares, setHighlightSquares] = useState({});
  const socketRef = useRef(null);
  const chessRef = useRef(new Chess());

  useEffect(() => {
    if (!username) {
      navigate("/");
      return;
    }

    socketRef.current = io(SOCKET_SERVER_URL);
    socketRef.current.emit("joinRoom", { roomId, username });

    socketRef.current.on("roleAssigned", (assignedRole) => {
      setRole(assignedRole);
    });

    socketRef.current.on("playersUpdate", (playersData) => {
      setPlayers(playersData);
    });

    socketRef.current.on("chessMove", ({ move }) => {
      const result = chessRef.current.move(move);
      if (result) {
        setFen(chessRef.current.fen());
        checkGameStatus();
      }
    });

    socketRef.current.on("turnUpdate", (newTurn) => {
      setTurn(newTurn);
    });

    socketRef.current.on("invalidMove", (msg) => {
      alert(msg);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [roomId, username, navigate]);

  const checkGameStatus = () => {
    const game = chessRef.current;

    if (game.isCheckmate()) {
      alert(`Checkmate! ${game.turn() === "w" ? "Black" : "White"} wins.`);
    } else if (game.isStalemate()) {
      alert("Stalemate! It's a draw.");
    } else if (game.isThreefoldRepetition()) {
      alert("Draw by threefold repetition.");
    } else if (game.isInsufficientMaterial()) {
      alert("Draw due to insufficient material.");
    } else if (game.isDraw()) {
      alert("Draw by 50-move rule or repetition.");
    }
  };

  const onPieceDrop = (sourceSquare, targetSquare) => {
    if (role !== turn) {
      alert("It's not your turn!");
      return false;
    }

    const move = {
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // Always promote to queen for simplicity
    };

    const tempChess = new Chess(chessRef.current.fen());
    const result = tempChess.move(move);

    if (result) {
      chessRef.current.move(move); // Apply valid move
      setFen(chessRef.current.fen());
      socketRef.current.emit("chessMove", { roomId, move, by: role });
      checkGameStatus();
      return true;
    } else {
      alert("Invalid move");
      return false;
    }
  };

  const onMouseOverSquare = (square) => {
    const moves = chessRef.current.moves({ square, verbose: true });
    if (moves.length === 0) {
      setHighlightSquares({});
      return;
    }

    const highlights = {};
    moves.forEach((m) => {
      highlights[m.to] = {
        background:
          chessRef.current.get(m.to) !== null
            ? "rgba(255, 0, 0, 0.4)"
            : "rgba(0, 255, 0, 0.3)",
      };
    });
    highlights[square] = { background: "rgba(0, 0, 255, 0.3)" };

    setHighlightSquares(highlights);
  };

  const onMouseOutSquare = () => {
    setHighlightSquares({});
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-900 text-white p-4">
      <h2 className="text-xl mb-4">Room: {roomId}</h2>
      <div className="flex flex-row gap-4">
        <div>
          <div className="mb-2">
            You are: <span className="font-bold">{role || "..."}</span>
          </div>
          <div className="mb-2">
            Turn: <span className="font-bold">{turn}</span>
          </div>
          <div className="mb-4">
            Players:
            <ul>
              <li>White: {players.white || "Waiting..."}</li>
              <li>Black: {players.black || "Waiting..."}</li>
            </ul>
          </div>
          <Chessboard
            position={fen === "start" ? undefined : fen}
            onPieceDrop={onPieceDrop}
            onMouseOverSquare={onMouseOverSquare}
            onMouseOutSquare={onMouseOutSquare}
            customSquareStyles={highlightSquares}
            boardOrientation={role === "white" ? "white" : "black"}
            transitionDuration={300}
            calcWidth={({ screenWidth }) => Math.min(500, screenWidth * 0.7)}
            draggable={role === turn}
          />
        </div>
        <Chat
          socket={socketRef.current}
          roomId={roomId}
          username={username}
        />
      </div>
    </div>
  );
};

export default Room;
