// src/components/Chat.js
import React, { useState, useEffect, useRef } from "react";

const Chat = ({ socket, roomId, username }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (!socket) return;

    const handleNewMessage = (message) => {
      setMessages((prev) => [...prev, message]);
    };

    socket.on("chatMessage", handleNewMessage);

    return () => {
      socket.off("chatMessage", handleNewMessage);
    };
  }, [socket]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = () => {
    if (input.trim() === "") return;

    const message = {
      username,
      message: input.trim(),
    };

    socket.emit("chatMessage", { roomId, username, message: input.trim() });

    setInput("");
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="flex flex-col w-80 h-[500px] bg-gray-800 rounded p-4">
      <h3 className="mb-2 font-bold text-lg">Chat</h3>
      <div className="flex-1 overflow-y-auto mb-2">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-1 px-2 py-1 rounded ${
              msg.username === username ? "bg-blue-600 self-end" : "bg-gray-700"
            } max-w-[80%]`}
          >
            <strong>{msg.username}: </strong>
            <span>{msg.message}</span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <textarea
        rows={2}
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Type a message..."
        className="resize-none rounded p-2 bg-gray-700 text-white focus:outline-none"
      />
      <button
        onClick={sendMessage}
        className="mt-2 bg-blue-600 hover:bg-blue-700 rounded px-4 py-2"
      >
        Send
      </button>
    </div>
  );
};

export default Chat;
