  const [fen, setFen] = useState("start");
  const [highlightSquares, setHighlightSquares] = useState({});
  const [boardWidth, setBoardWidth] = useState(400);
  const [players, setPlayers] = useState({ white: null, black: null });
  const [gameStarted, setGameStarted] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState("connecting");
  const socketRef = useRef(null);
  const chessRef = useRef(new Chess());
  const containerRef = useRef(null);
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;

  // Calculate responsive board width
  useEffect(() => {
    const calculateBoardWidth = () => {
      if (containerRef.current) {
        const containerWidth = containerRef.current.offsetWidth;
        const padding = 32;
        const maxWidth = Math.min(containerWidth - padding, 400);
        setBoardWidth(Math.max(maxWidth, 280));
      }
    };

    calculateBoardWidth();
    window.addEventListener('resize', calculateBoardWidth);
    
    return () => {
      window.removeEventListener('resize', calculateBoardWidth);
    };
  }, []);

  // Socket connection management
  useEffect(() => {
    if (!username) {
      navigate("/");
      return;
    }

    const connectSocket = () => {
      setConnectionStatus("connecting");
      
      // Disconnect existing socket if any
      if (socketRef.current) {
        socketRef.current.disconnect();
      }

      socketRef.current = io(SOCKET_SERVER_URL, {
        // Improved connection options
        transports: ['websocket', 'polling'],
        timeout: 20000,
        forceNew: true, // Force new connection
        reconnection: true,
        reconnectionDelay: 1000,
        reconnectionAttempts: maxReconnectAttempts,
        maxReconnectionAttempts: maxReconnectAttempts,
      });

      socketRef.current.on("connect", () => {
        setConnectionStatus("connected");
        reconnectAttempts.current = 0;
        console.log("Connected to server");
        
        // Join room immediately after connection
        socketRef.current.emit("joinRoom", { roomId, username });
      });

      socketRef.current.on("disconnect", (reason) => {
        setConnectionStatus("disconnected");
        console.log("Disconnected from server, reason:", reason);
        
        // Handle different disconnect reasons
        if (reason === "io server disconnect") {
          // Server forcefully disconnected - try to reconnect
          setTimeout(connectSocket, 2000);
        }
      });

      socketRef.current.on("connect_error", (error) => {
        console.log("Connection error:", error);
        setConnectionStatus("error");
        reconnectAttempts.current++;
        
        if (reconnectAttempts.current >= maxReconnectAttempts) {
          setConnectionStatus("failed");
          alert("Failed to connect to server. Please refresh the page.");
        }
      });

      socketRef.current.on("reconnect", (attemptNumber) => {
        console.log("Reconnected after", attemptNumber, "attempts");
        setConnectionStatus("connected");
      });

      socketRef.current.on("reconnect_error", (error) => {
        console.log("Reconnection error:", error);
      });

      socketRef.current.on("roleAssigned", (assignedRole) => {
        console.log("Role assigned:", assignedRole);
        setRole(assignedRole);
      });

      socketRef.current.on("turnUpdate", (newTurn) => {
        console.log("Turn update:", newTurn);
        setTurn(newTurn);
      });

      socketRef.current.on("playersUpdate", (playersData) => {
        console.log("Players update:", playersData);
        setPlayers(playersData);
        console.log(player)
      });

      socketRef.current.on("gameStarted", (started) => {
        console.log("Game started:", started);
        setGameStarted(started);
      });

      socketRef.current.on("chessMove", ({ move, by }) => {
        console.log("Chess move received:", move, "by:", by);
        if (by !== role) {
          try {
            const result = chessRef.current.move(move);
            if (result) {
              setFen(chessRef.current.fen());
              setTurn(chessRef.current.turn() === "w" ? "white" : "black");
              checkGameStatus();
            }
          } catch (error) {
            console.error("Error applying move:", error, move);
          }
        }
      });

      socketRef.current.on("invalidMove", (msg) => {
        alert(msg);
      });
    };

    connectSocket();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
        socketRef.current = null;
      }
    };
  }, [roomId, username, navigate]);

  const checkGameStatus = () => {
    if (chessRef.current.isCheckmate()) {
      alert(`Checkmate! ${turn === "white" ? "Black" : "White"} wins.`);
    } else if (chessRef.current.isStalemate()) {
      alert("Stalemate!");
    } else if (chessRef.current.isCheck()) {
      alert("Check!");
    }
  };

  const onPieceDrop = (sourceSquare, targetSquare) => {
    if (connectionStatus !== "connected") {
      alert("Not connected to server");
      return false;
    }

    if (role === "viewer") {
      alert("You are a viewer and cannot make moves");
      return false;
    }

    if (role !== turn) {
      alert("It's not your turn");
      return false;
    }

    if (!gameStarted) {
      alert("Waiting for another player to join");
      return false;
    }

    const move = {
      from: sourceSquare,
      to: targetSquare,
      promotion: "q",
    };

    try {
      const result = chessRef.current.move(move);

      if (result) {
        setFen(chessRef.current.fen());
        setTurn(chessRef.current.turn() === "w" ? "white" : "black");
        socketRef.current.emit("chessMove", { roomId, move, by: role });
        checkGameStatus();
        return true;
      } else {
        alert("Invalid move");
        return false;
      }
    } catch (error) {
      console.error("Move error:", error);
      alert("Invalid move");
      return false;
    }
  };

  const onMouseOverSquare = (square) => {
    if (role === "viewer" || role !== turn || !gameStarted || connectionStatus !== "connected") {
      return;
    }

    const moves = chessRef.current.moves({ square, verbose: true });
    if (moves.length === 0) {
      setHighlightSquares({});
      return;
    }

    const highlights = {};
    moves.forEach((m) => {
      highlights[m.to] = {
        background:
          chessRef.current.get(m.to) !== null
            ? "rgba(255, 0, 0, 0.4)"
            : "rgba(0, 255, 0, 0.3)",
      };
    });
    highlights[square] = { background: "rgba(0, 0, 255, 0.3)" };

    setHighlightSquares(highlights);
  };

  const onMouseOutSquare = () => {
    setHighlightSquares({});
  };

  const getStatusMessage = () => {
    if (connectionStatus === "connecting") {
      return "Connecting to server...";
    }
    if (connectionStatus === "disconnected") {
      return "Disconnected - trying to reconnect...";
    }
    if (connectionStatus === "error") {
      return "Connection error - retrying...";
    }
    if (connectionStatus === "failed") {
      return "Connection failed - please refresh";
    }
    if (connectionStatus !== "connected") {
      return `Connection: ${connectionStatus}`;
    }
    if (!gameStarted) {
      return `Waiting for ${!players.white ? 'white' : 'black'} player...`;
    }
    if (role === "viewer") {
      return "You are viewing the game";
    }
    return `${turn}'s turn`;
  };

  const getConnectionStatusColor = () => {
    switch (connectionStatus) {
      case "connected": return "green";
      case "connecting": return "orange";
      case "disconnected": return "red";
      case "error": return "red";
      case "failed": return "darkred";
      default: return "gray";
    }
  };

  return (
    <div className="room-container">
      <h2 className="room-title">Room: {roomId}</h2>
      <div className="room-content">
        <div className="chessboard-wrapper" ref={containerRef}>
          <div className="info-panel">
            <p>
              You are: <span className="bold-text">{role || "..."}</span>
            </p>
            <p>
              Status: <span className="bold-text" style={{color: getConnectionStatusColor()}}>{getStatusMessage()}</span>
            </p>
            <div className="players-info">
              <p>White: {players.white || "Waiting..."}</p>
              <p>Black: {players.black || "Waiting..."}</p>
            </div>
          </div>
          <div className="chessboard-container">
            <Chessboard
              position={fen === "start" ? undefined : fen}
              boardWidth={boardWidth}
              onPieceDrop={onPieceDrop}
              onMouseOverSquare={onMouseOverSquare}
              onMouseOutSquare={onMouseOutSquare}
              customSquareStyles={highlightSquares}
              boardOrientation={role === "white" ? "white" : "black"}
              transitionDuration={300}
              draggable={role !== "viewer" && role === turn && gameStarted && connectionStatus === "connected"}
            />
          </div>
        </div>
        <Chat socket={socketRef.current} roomId={roomId} username={username} />
      </div>
    </div>
  );
};

export default Room;
