import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function generateRoomId() {
  // simple random 6-character alphanumeric
  return Math.random().toString(36).substring(2, 8).toUpperCase();
}

const Home = () => {
  const [roomId, setRoomId] = useState("");
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  const handleJoin = () => {
    if (roomId.trim() && username.trim()) {
      localStorage.setItem("username", username.trim());
      navigate(`/room/${roomId.trim()}`);
    }
  };

  const handleCreate = () => {
    if (!username.trim()) {
      alert("Please enter username first");
      return;
    }
    const newRoomId = generateRoomId();
    localStorage.setItem("username", username.trim());
    navigate(`/room/${newRoomId}`);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-white px-4">
      <h1 className="text-3xl font-bold mb-6">Real-Time Chess Game</h1>
      <input
        type="text"
        placeholder="Enter Room ID"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
        className="mb-3 px-4 py-2 rounded bg-gray-700 text-white w-full max-w-xs"
      />
      <input
        type="text"
        placeholder="Enter Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        className="mb-3 px-4 py-2 rounded bg-gray-700 text-white w-full max-w-xs"
      />
      <div className="flex gap-4">
        <button
          onClick={handleJoin}
          className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded"
        >
          Join Room
        </button>
        <button
          onClick={handleCreate}
          className="px-4 py-2 bg-green-600 hover:bg-green-700 rounded"
        >
          Create New Room
        </button>
      </div>
    </div>
  );
};

export default Home;
