const app = express();
const server = http.createServer(app);
const io = new Server(server, {
  cors: {
    origin: "*",
    methods: ["GET", "POST"]
  }
});
const rooms = {};
io.on("connection", socket => {
  console.log(`Client connected: ${socket.id}`);
  socket.on("joinRoom", ({
    roomId,
    username
  }) => {
    socket.join(roomId);
    socket.data.username = username;
    socket.data.roomId = roomId;
    if (!rooms[roomId]) {
      rooms[roomId] = {
        white: null,
        black: null,
        turn: "white",
        fen: "start" // 👈 default game state
      };
    }
    const room = rooms[roomId];

    // Handle reconnect: remove stale assignment
    if (room.white && room.white.id === socket.id) room.white = null;
    if (room.black && room.black.id === socket.id) room.black = null;
    let assignedRole = "viewer";
    if (!room.white) {
      room.white = {
        id: socket.id,
        username
      };
      assignedRole = "white";
    } else if (!room.black) {
      room.black = {
        id: socket.id,
        username
      };
      assignedRole = "black";
    }
    socket.emit("roleAssigned", assignedRole);
    socket.emit("turnUpdate", room.turn);
    socket.emit("fenUpdate", room.fen); // 👈 emit FEN to rejoiners

