    if (fen) {
      room.fen = fen;
    }
    io.to(roomId).emit("chessMove", {
      move,
      by
    });
    room.turn = room.turn === "white" ? "black" : "white";
    io.to(roomId).emit("turnUpdate", room.turn);
  });
