import React, { useEffect, useState } from "react";

const ChatBox = ({ socket, roomId, username }) => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    if (!socket) return;

    const handleChat = ({ username, message }) => {
      setMessages((prev) => [...prev, { username, message }]);
    };

    socket.on("chatMessage", handleChat);

    return () => {
      socket.off("chatMessage", handleChat);
    };
  }, [socket]);

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("chatMessage", { roomId, username, message: message.trim() });
      setMessage("");
    }
  };

  return (
    <div className="bg-gray-900 rounded-lg p-4 h-full flex flex-col">
      <h2 className="text-lg font-bold mb-2">Chat</h2>
      <div className="flex-1 overflow-y-auto mb-2 space-y-1 text-sm">
        {messages.map((msg, idx) => (
          <div key={idx}>
            <span className="font-semibold">{msg.username}: </span>
            <span>{msg.message}</span>
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="flex-1 px-3 py-1 rounded bg-gray-700 text-white"
          placeholder="Type a message"
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button
          onClick={sendMessage}
          className="bg-blue-600 px-3 py-1 rounded hover:bg-blue-700"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
