// src/components/Room.js
import { Chessboard } from "react-chessboard";
import React, { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import Chat from "./Chat";
import { Chess } from "chess.js"; // ✅ Correct import for v1.3.1

const SOCKET_SERVER_URL = "http://localhost:4000";

const Room = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const username = localStorage.getItem("username") || "";
  const [role, setRole] = useState(null);
  const [players, setPlayers] = useState({ white: null, black: null });
  const [turn, setTurn] = useState("white");
  const [fen, setFen] = useState("start");
  const [highlightSquares, setHighlightSquares] = useState({});
  const [showPromotionDialog, setShowPromotionDialog] = useState(false);
  const [promotionMove, setPromotionMove] = useState(null);
  const socketRef = useRef(null);
  const chessRef = useRef(new Chess());

  useEffect(() => {
    if (!username) {
      navigate("/");
      return;
    }

    socketRef.current = io(SOCKET_SERVER_URL);
    socketRef.current.emit("joinRoom", { roomId, username });

    socketRef.current.on("roleAssigned", (assignedRole) => {
      setRole(assignedRole);
    });

    socketRef.current.on("playersUpdate", (playersData) => {
      setPlayers(playersData);
    });

    socketRef.current.on("chessMove", ({ move, by }) => {
      // Only apply the move if it's from the other player
      if (by !== role) {
        try {
          const result = chessRef.current.move(move);
          if (result) {
            setFen(chessRef.current.fen());
            // Update turn after opponent's move
            setTurn(chessRef.current.turn() === 'w' ? 'white' : 'black');
            checkGameStatus();
          } else {
            console.error("Invalid move received:", move);
          }
        } catch (error) {
          console.error("Error applying move:", error, move);
        }
      }
    });

    socketRef.current.on("turnUpdate", (newTurn) => {
      setTurn(newTurn);
    });

    socketRef.current.on("invalidMove", (msg) => {
      alert(msg);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [roomId, username, navigate]);

  const checkGameStatus = () => {
    if (chessRef.current.isCheckmate()) {
      alert(`Checkmate! ${turn === "white" ? "Black" : "White"} wins.`);
    } else if (chessRef.current.isStalemate()) {
      alert("Stalemate!");
    } else if (chessRef.current.isCheck()) {
      alert("Check!");
    }
  };

  const isPromotionMove = (sourceSquare, targetSquare) => {
    const piece = chessRef.current.get(sourceSquare);
    if (!piece || piece.type !== 'p') return false;
    
    const targetRank = parseInt(targetSquare[1]);
    if (piece.color === 'w' && targetRank === 8) return true;
    if (piece.color === 'b' && targetRank === 1) return true;
    
    return false;
  };

  const handlePromotion = (piece) => {
    if (!promotionMove) return;

    const move = {
      from: promotionMove.from,
      to: promotionMove.to,
      promotion: piece,
    };

    try {
      const result = chessRef.current.move(move);

      if (result) {
        setFen(chessRef.current.fen());
        setTurn(chessRef.current.turn() === 'w' ? 'white' : 'black');
        socketRef.current.emit("chessMove", { roomId, move, by: role });
        checkGameStatus();
      } else {
        alert("Invalid move");
      }
    } catch (error) {
      console.error("Promotion error:", error);
      alert("Invalid move");
    }

    setShowPromotionDialog(false);
    setPromotionMove(null);
  };

  const onPieceDrop = (sourceSquare, targetSquare) => {
    if (role !== turn) {
      alert("It's not your turn");
      return false;
    }

    // Check if this is a promotion move
    if (isPromotionMove(sourceSquare, targetSquare)) {
      setPromotionMove({ from: sourceSquare, to: targetSquare });
      setShowPromotionDialog(true);
      return false; // Don't make the move yet, wait for promotion choice
    }

    const move = {
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // Default for non-pawn moves
    };

    try {
      const result = chessRef.current.move(move);

      if (result) {
        setFen(chessRef.current.fen());
        setTurn(chessRef.current.turn() === 'w' ? 'white' : 'black');
        socketRef.current.emit("chessMove", { roomId, move, by: role });
        checkGameStatus();
        return true;
      } else {
        alert("Invalid move");
        return false;
      }
    } catch (error) {
      console.error("Move error:", error);
      alert("Invalid move");
      return false;
    }
  };

  const onMouseOverSquare = (square) => {
    const moves = chessRef.current.moves({ square, verbose: true });
    if (moves.length === 0) {
      setHighlightSquares({});
      return;
    }

    const highlights = {};
    moves.forEach((m) => {
      highlights[m.to] = {
        background:
          chessRef.current.get(m.to) !== null ? "rgba(255, 0, 0, 0.4)" : "rgba(0, 255, 0, 0.3)",
      };
    });
    highlights[square] = { background: "rgba(0, 0, 255, 0.3)" };

    setHighlightSquares(highlights);
  };

  const onMouseOutSquare = () => {
    setHighlightSquares({});
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-900 text-white p-4">
      <h2 className="text-xl mb-4">Room: {roomId}</h2>
      <div className="flex flex-row gap-4">
        <div>
          <div className="mb-2">
            You are: <span className="font-bold">{role || "..."}</span>
          </div>
          <div className="mb-2">
            Turn: <span className="font-bold">{turn}</span>
          </div>
          <div className="mb-4">
            Players:
            <ul>
              <li>White: {players.white || "Waiting..."}</li>
              <li>Black: {players.black || "Waiting..."}</li>
            </ul>
          </div>
          <Chessboard
            position={fen === "start" ? undefined : fen}
            onPieceDrop={onPieceDrop}
            onMouseOverSquare={onMouseOverSquare}
            onMouseOutSquare={onMouseOutSquare}
            customSquareStyles={highlightSquares}
            boardOrientation={role === "white" ? "white" : "black"}
            transitionDuration={300}
            calcWidth={({ screenWidth }) => Math.min(500, screenWidth * 0.7)}
            draggable={role === turn && !showPromotionDialog}
          />
        </div>
        <Chat socket={socketRef.current} roomId={roomId} username={username} />
      </div>

      {/* Promotion Dialog */}
      {showPromotionDialog && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-black">Choose promotion piece:</h3>
            <div className="flex gap-4">
              <button
                onClick={() => handlePromotion('q')}
                className="p-4 bg-blue-500 hover:bg-blue-600 text-white rounded-lg text-4xl"
                title="Queen"
              >
                ♕
              </button>
              <button
                onClick={() => handlePromotion('r')}
                className="p-4 bg-blue-500 hover:bg-blue-600 text-white rounded-lg text-4xl"
                title="Rook"
              >
                ♖
              </button>
              <button
                onClick={() => handlePromotion('b')}
                className="p-4 bg-blue-500 hover:bg-blue-600 text-white rounded-lg text-4xl"
                title="Bishop"
              >
                ♗
              </button>
              <button
                onClick={() => handlePromotion('n')}
                className="p-4 bg-blue-500 hover:bg-blue-600 text-white rounded-lg text-4xl"
                title="Knight"
              >
                ♘
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
