import React, { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import { io } from "socket.io-client";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

const socket = io("http://localhost:4000"); // Replace with deployed server if needed

const Room = () => {
  const { roomId } = useParams();
  const username = localStorage.getItem("username") || "Anonymous";
  const [fen, setFen] = useState("start");
  const [game] = useState(new Chess());
  const [chatMessages, setChatMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [role, setRole] = useState("viewer");
  const [players, setPlayers] = useState({ white: null, black: null });

  const chatRef = useRef(null);

  useEffect(() => {
    socket.emit("joinRoom", { roomId, username });

    socket.on("assignRole", (newRole) => setRole(newRole));
    socket.on("roleUpdate", (playerRoles) => setPlayers(playerRoles));

    socket.on("chessMove", (move) => {
      game.move(move);
      setFen(game.fen());
    });

    socket.on("chatMessage", (msg) => {
      setChatMessages((prev) => [...prev, msg]);
      scrollChatToBottom();
    });

    return () => {
      socket.disconnect();
    };
  }, [roomId]);

  const onDrop = (sourceSquare, targetSquare) => {
    if ((role === "white" && game.turn() !== "w") || (role === "black" && game.turn() !== "b")) return false;
    const move = game.move({ from: sourceSquare, to: targetSquare, promotion: "q" });
    if (move) {
      setFen(game.fen());
      socket.emit("chessMove", { roomId, move });
      return true;
    }
    return false;
  };

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("chatMessage", { roomId, username, message });
      setMessage("");
    }
  };

  const scrollChatToBottom = () => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  };

  return (
    <div className="flex flex-col md:flex-row min-h-screen bg-gray-900 text-white">
      <div className="flex flex-col items-center justify-center w-full md:w-2/3 p-4">
        <h2 className="text-xl mb-2">Room: {roomId}</h2>
        <h3 className="mb-4">
          You are playing as: <strong>{role}</strong>
        </h3>
        <Chessboard position={fen} onPieceDrop={onDrop} boardOrientation={role} />
        <div className="mt-4">
          <p>
            <strong>White:</strong> {players.white || "Waiting..."}
          </p>
          <p>
            <strong>Black:</strong> {players.black || "Waiting..."}
          </p>
        </div>
      </div>

      <div className="flex flex-col w-full md:w-1/3 border-l border-gray-700 p-4">
        <h3 className="text-lg font-semibold mb-2">Chat</h3>
        <div
          ref={chatRef}
          className="flex-1 overflow-y-auto bg-gray-800 p-3 rounded mb-2"
        >
          {chatMessages.map((msg, idx) => (
            <div key={idx}>
              <span className="text-blue-400 font-medium">{msg.username}: </span>
              <span>{msg.message}</span>
            </div>
          ))}
        </div>
        <div className="flex">
          <input
            className="flex-1 p-2 bg-gray-700 text-white rounded-l"
            type="text"
            value={message}
            placeholder="Type a message..."
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage} className="bg-blue-600 px-4 rounded-r">
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Room;
