// src/components/Home.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [roomId, setRoomId] = useState("");
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  const handleJoin = () => {
    if (roomId.trim() && username.trim()) {
      localStorage.setItem("username", username.trim());
      navigate(`/room/${roomId.trim()}`);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-tr from-gray-900 via-gray-800 to-gray-900 text-white px-6 sm:px-12">
      <h1 className="text-4xl sm:text-5xl font-extrabold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 select-none">
        Real-Time Chess Game
      </h1>

      <div className="w-full max-w-md bg-gray-800 bg-opacity-70 rounded-2xl p-8 shadow-lg backdrop-blur-sm">
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          className="mb-5 px-5 py-3 rounded-lg bg-gradient-to-r from-purple-700 via-purple-800 to-purple-900 placeholder-purple-300 text-white text-lg focus:outline-none focus:ring-2 focus:ring-pink-500 transition duration-300 w-full"
        />
        <input
          type="text"
          placeholder="Enter Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="mb-7 px-5 py-3 rounded-lg bg-gradient-to-r from-purple-700 via-purple-800 to-purple-900 placeholder-purple-300 text-white text-lg focus:outline-none focus:ring-2 focus:ring-pink-500 transition duration-300 w-full"
        />
        <button
          onClick={handleJoin}
          className="w-full py-3 rounded-lg bg-gradient-to-r from-pink-500 via-red-500 to-yellow-500 hover:from-pink-600 hover:via-red-600 hover:to-yellow-600 active:scale-95 transform transition duration-200 font-semibold text-lg shadow-md"
          aria-label="Join Room"
        >
          Join Room
        </button>
      </div>

      <p className="mt-10 text-sm text-gray-400 select-none text-center max-w-xs sm:max-w-md">
        Join or create a room with your unique ID and username to start playing.
      </p>
    </div>
  );
};

export default Home;
