import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";
import React, { useEffect, useRef, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import Chat from "./Chat";

const SOCKET_SERVER_URL = "http://localhost:4000";

const Room = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const username = localStorage.getItem("username") || "";
  const [role, setRole] = useState(null);
  const [players, setPlayers] = useState({ white: null, black: null });
  const [turn, setTurn] = useState("white");
  const [fen, setFen] = useState("start");
  const [selectedSquare, setSelectedSquare] = useState(null);
  const [legalMoves, setLegalMoves] = useState([]);
  const [gameStatus, setGameStatus] = useState("");

  const socketRef = useRef(null);
  const chessRef = useRef(new Chess());

  useEffect(() => {
    if (!username) {
      navigate("/");
      return;
    }

    socketRef.current = io(SOCKET_SERVER_URL);
    socketRef.current.emit("joinRoom", { roomId, username });

    socketRef.current.on("roleAssigned", (assignedRole) => {
      setRole(assignedRole);
    });

    socketRef.current.on("playersUpdate", (playersData) => {
      setPlayers(playersData);
    });

    socketRef.current.on("chessMove", ({ move, by }) => {
      // Validate if move is legal before applying
      const result = chessRef.current.move(move);
      if (!result) {
        console.warn("Received invalid move from server", move);
        return;
      }

      setFen(chessRef.current.fen());

      if (chessRef.current.inCheckmate()) {
        setGameStatus(`${by} wins by checkmate`);
      } else if (chessRef.current.inCheck()) {
        setGameStatus(`${by === "white" ? "black" : "white"} is in check`);
      } else {
        setGameStatus("");
      }

      setTurn(by === "white" ? "black" : "white");
      setSelectedSquare(null);
      setLegalMoves([]);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [roomId, username, navigate]);

  const onPieceDrop = (sourceSquare, targetSquare) => {
    if (role !== turn) {
      alert("It's not your turn");
      return false;
    }

    const move = {
      from: sourceSquare,
      to: targetSquare,
      promotion: "q",
    };

    const result = chessRef.current.move(move);
    if (!result) {
      return false;
    }

    setFen(chessRef.current.fen());
    socketRef.current.emit("chessMove", { roomId, move, by: role });

    if (chessRef.current.inCheckmate()) {
      setGameStatus(`${role} wins by checkmate`);
    } else if (chessRef.current.inCheck()) {
      setGameStatus(`${role === "white" ? "black" : "white"} is in check`);
    } else {
      setGameStatus("");
    }

    setSelectedSquare(null);
    setLegalMoves([]);
    setTurn(role === "white" ? "black" : "white");

    return true;
  };

  const onSquareClick = (square) => {
    const moves = chessRef.current.moves({ square, verbose: true });
    if (moves.length === 0) {
      setSelectedSquare(null);
      setLegalMoves([]);
      return;
    }

    setSelectedSquare(square);
    setLegalMoves(moves.map((m) => m.to));
  };

  const squareStyles = {};
  legalMoves.forEach((sq) => {
    squareStyles[sq] = {
      backgroundColor: "rgba(0,255,0,0.4)",
    };
  });

  if (selectedSquare) {
    squareStyles[selectedSquare] = {
      backgroundColor: "rgba(255,255,0,0.4)",
    };
  }

  return (
    <div className="min-h-screen flex flex-col bg-gray-900 text-white p-4">
      <h2 className="text-xl mb-4">Room: {roomId}</h2>
      <div className="flex flex-row gap-4">
        <div>
          <div className="mb-2">
            You are: <span className="font-bold">{role || "..."}</span>
          </div>
          <div className="mb-2">
            Turn: <span className="font-bold">{turn}</span>
          </div>
          <div className="mb-4">
            Players:
            <ul>
              <li>White: {players.white || "Waiting..."}</li>
              <li>Black: {players.black || "Waiting..."}</li>
            </ul>
          </div>

          <Chessboard
            position={fen}
            onPieceDrop={onPieceDrop}
            onSquareClick={onSquareClick}
            boardOrientation={role === "white" ? "white" : "black"}
            customSquareStyles={squareStyles}
            draggable={role === turn}
            transitionDuration={300}
            calcWidth={({ screenWidth }) => Math.min(500, screenWidth * 0.7)}
          />

          {gameStatus && (
            <div className="mt-4 text-lg text-red-400 font-bold">
              {gameStatus}
            </div>
          )}
        </div>
        <Chat socket={socketRef.current} roomId={roomId} username={username} />
      </div>
    </div>
  );
};

export default Room;
