import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { Chessboard } from "react-chessboard";
import io from "socket.io-client";
import ChatBox from "../components/ChatBox";

const socket = io("http://localhost:4000");

const GameRoom = () => {
  const { roomId } = useParams();
  const username = localStorage.getItem("username");
  const [role, setRole] = useState("viewer");
  const [game, setGame] = useState(new window.Chess());
  const boardRef = useRef(null);
  const [turn, setTurn] = useState("white");

  useEffect(() => {
    if (username && roomId) {
      socket.emit("joinRoom", { roomId, username });
    }

    socket.on("roleAssigned", (assignedRole) => {
      setRole(assignedRole);
    });

    socket.on("chessMove", ({ move, by }) => {
      game.move(move);
      setGame(new window.Chess(game.fen()));
      setTurn(game.turn() === "w" ? "white" : "black");
    });

    socket.on("invalidMove", (msg) => {
      alert(msg);
    });

    return () => {
      socket.disconnect();
    };
  }, [roomId, username]);

  const makeMove = (from, to) => {
    const move = { from, to, promotion: "q" };
    const tempGame = new window.Chess(game.fen());
    const result = tempGame.move(move);

    if (result && role === turn) {
      socket.emit("chessMove", { roomId, move });
    }
  };

  return (
    <div className="flex flex-col md:flex-row min-h-screen bg-gray-800 text-white">
      <div className="flex-1 flex items-center justify-center p-4">
        <div>
          <p className="text-center mb-2 font-semibold">
            Role: {role} | Turn: {turn}
          </p>
          <Chessboard
            position={game.fen()}
            onPieceDrop={(sourceSquare, targetSquare) =>
              makeMove(sourceSquare, targetSquare)
            }
            arePiecesDraggable={role === turn}
            boardOrientation={role}
            boardWidth={400}
          />
        </div>
      </div>
      <div className="w-full md:w-1/3 p-4">
        <ChatBox socket={socket} roomId={roomId} username={username} />
      </div>
    </div>
  );
};

export default GameRoom;
